CREATE TYPE "Platform" AS ENUM ('AIRBNB','BOOKING','VRBO','MANUAL');
CREATE TABLE "Account" ("id" TEXT PRIMARY KEY, "email" TEXT NOT NULL UNIQUE, "name" TEXT, "createdAt" TIMESTAMP NOT NULL DEFAULT NOW());
CREATE TABLE "Listing" ("id" TEXT PRIMARY KEY, "accountId" TEXT NOT NULL, "name" TEXT NOT NULL, "platform" "Platform", CONSTRAINT "Listing_accountId_fkey" FOREIGN KEY ("accountId") REFERENCES "Account"("id") ON DELETE CASCADE ON UPDATE CASCADE);
CREATE TABLE "IcalUrl" ("id" TEXT PRIMARY KEY, "listingId" TEXT NOT NULL, "url" TEXT NOT NULL, "source" "Platform" NOT NULL, "createdAt" TIMESTAMP NOT NULL DEFAULT NOW(), CONSTRAINT "IcalUrl_listingId_fkey" FOREIGN KEY ("listingId") REFERENCES "Listing"("id") ON DELETE CASCADE ON UPDATE CASCADE);
CREATE TABLE "Reservation" ("id" TEXT PRIMARY KEY, "listingId" TEXT NOT NULL, "start" TIMESTAMP NOT NULL, "end" TIMESTAMP NOT NULL, "source" "Platform" NOT NULL, "externalId" TEXT, "guestName" TEXT, "createdAt" TIMESTAMP NOT NULL DEFAULT NOW(), CONSTRAINT "Reservation_listingId_fkey" FOREIGN KEY ("listingId") REFERENCES "Listing"("id") ON DELETE CASCADE ON UPDATE CASCADE);
CREATE INDEX "Reservation_listing_start_end_idx" ON "Reservation" ("listingId","start","end");
CREATE TABLE "Payout" ("id" TEXT PRIMARY KEY, "accountId" TEXT NOT NULL, "listingId" TEXT, "platform" "Platform" NOT NULL, "date" TIMESTAMP NOT NULL, "grossCents" INTEGER NOT NULL, "feesCents" INTEGER NOT NULL, "netCents" INTEGER NOT NULL, "currency" TEXT NOT NULL, "createdAt" TIMESTAMP NOT NULL DEFAULT NOW(), CONSTRAINT "Payout_accountId_fkey" FOREIGN KEY ("accountId") REFERENCES "Account"("id") ON DELETE CASCADE ON UPDATE CASCADE);
CREATE TABLE "Message" ("id" TEXT PRIMARY KEY, "accountId" TEXT NOT NULL, "threadId" TEXT NOT NULL, "platform" "Platform", "fromEmail" TEXT, "toEmail" TEXT, "subject" TEXT, "body" TEXT NOT NULL, "timestamp" TIMESTAMP NOT NULL DEFAULT NOW(), CONSTRAINT "Message_accountId_fkey" FOREIGN KEY ("accountId") REFERENCES "Account"("id") ON DELETE CASCADE ON UPDATE CASCADE);
